import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

function DocumentEditor() {
  const [content, setContent] = useState("");
  const [username, setUsername] = useState("");
  const [typingUsers, setTypingUsers] = useState([]);

  useEffect(() => {
    // Ask user for a username when they first visit
    const user = prompt("Enter your username:");
    setUsername(user);
    socket.emit("set-username", user);

    socket.on("document", (newContent) => {
      setContent(newContent);
    });

    socket.on("typing-users", (users) => {
      setTypingUsers(users);
    });
  }, []);

  const handleChange = (event) => {
    setContent(event.target.value);
    socket.emit("document", event.target.value);
    socket.emit("typing");

    // Stop typing after 2 seconds of inactivity
    clearTimeout(window.typingTimeout);
    window.typingTimeout = setTimeout(() => {
      socket.emit("stop-typing");
    }, 2000);
  };

  return (
    <div className="editor-container">
      <textarea
        className="editor"
        value={content}
        onChange={handleChange}
        placeholder="Start typing..."
      ></textarea>
      <div className="typing-indicator">
        {typingUsers.length > 0 && (
          <p>{typingUsers.join(", ")} {typingUsers.length > 1 ? "are" : "is"} typing...</p>
        )}
      </div>
    </div>
  );
}

export default DocumentEditor;
