import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import '../App.css'; // Make sure the CSS file is correctly imported
const socket = io("http://localhost:5000");

function DocumentEditor() {
  const [content, setContent] = useState("");
  const [typingUser, setTypingUser] = useState(null);
  const [username, setUsername] = useState("");
  const [isUsernameSet, setIsUsernameSet] = useState(false); // Track if username is set

  useEffect(() => {
    // Listen for document changes
    socket.on("document", (newContent) => {
      setContent(newContent);
    });

    // Listen for typing event
    socket.on("typing", (username) => {
      setTypingUser(username); // Set the typing user when someone starts typing
    });

    // Listen for stop-typing event
    socket.on("stop-typing", () => {
      setTypingUser(null); // Clear the typing user when they stop typing
    });

    return () => {
      socket.off("document");
      socket.off("typing");
      socket.off("stop-typing");
    };
  }, []);

  const handleChange = (event) => {
    setContent(event.target.value);
    socket.emit("document", event.target.value);

    if (event.target.value.length > 0) {
      // Emit typing event if there's content being typed
      socket.emit("typing", username);
    } else {
      // Emit stop-typing event if input is empty
      socket.emit("stop-typing");
    }
  };

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleUsernameSubmit = (event) => {
    if (event.key === "Enter" && username.trim() !== "") {
      setIsUsernameSet(true); // Set username and switch to editor
    }
  };

  return (
    <div className="editor-container">
      {!isUsernameSet ? (
        <div className="username-input">
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={handleUsernameChange}
            onKeyDown={handleUsernameSubmit} // Listen for Enter key
          />
        </div>
      ) : (
        <>
          <textarea
            className="editor"
            value={content}
            onChange={handleChange}
            placeholder="Start typing..."
          ></textarea>

          {typingUser && typingUser !== username && (
            <div className="typing-indicator">
              {typingUser} is typing...
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default DocumentEditor;
