// DocumentEditor.js
import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import "../App.css";

const socket = io("http://localhost:5000");

function DocumentEditor() {
  const [content, setContent] = useState("");
  const [typingUser, setTypingUser] = useState(null);
  const [username, setUsername] = useState("");
  const [isUsernameSet, setIsUsernameSet] = useState(false);
  const [activeUsers, setActiveUsers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [cursorPositions, setCursorPositions] = useState({});
  const [typingTimeout, setTypingTimeout] = useState(null);

  const textAreaRef = useRef(null);

  useEffect(() => {
    socket.on("document", (newContent) => {
      setContent(newContent);
    });

    socket.on("typing", (username) => {
      setTypingUser(username);
    });

    socket.on("stop-typing", () => {
      setTypingUser(null);
    });

    socket.on("user-list", (users) => {
      setActiveUsers(users);
    });

    socket.on("user-message", (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    socket.on("cursor-position", (data) => {
      setCursorPositions((prevPositions) => ({
        ...prevPositions,
        [data.username]: data.position,
      }));
    });

    return () => {
      socket.off("document");
      socket.off("typing");
      socket.off("stop-typing");
      socket.off("user-list");
      socket.off("user-message");
      socket.off("cursor-position");
    };
  }, []);

  const handleChange = (event) => {
    setContent(event.target.value);
    socket.emit("document", event.target.value);

    if (event.target.value.length > 0 && username) {
      socket.emit("typing", username);

      if (typingTimeout) {
        clearTimeout(typingTimeout);
      }

      setTypingTimeout(
        setTimeout(() => {
          socket.emit("stop-typing", username);
        }, 700)
      );
    } else {
      socket.emit("stop-typing", username);
    }
  };

  const handleCursorPosition = () => {
    const cursorPosition = textAreaRef.current.selectionStart;
    socket.emit("cursor-position", { username, position: cursorPosition });
  };

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleUsernameSubmit = (event) => {
    if (event.key === "Enter" && username.trim() !== "") {
      setIsUsernameSet(true);
      socket.emit("set-username", username);
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => {
        const fileContent = e.target.result;
        setContent(fileContent); // Load the file content into the editor
        socket.emit("document", fileContent); // Broadcast the content to other users
      };
      reader.readAsText(file);
    } else {
      alert("Please upload a valid .txt file.");
    }
  };

  const getCursorPosition = (position) => {
    if (!textAreaRef.current) return { top: 0, left: 0 };
    const textArea = textAreaRef.current;
    const textBeforeCursor = textArea.value.slice(0, position);
    const span = document.createElement("span");
    span.style.visibility = "hidden";
    span.style.whiteSpace = "pre-wrap";
    span.style.font = "inherit";
    span.textContent = textBeforeCursor;
    document.body.appendChild(span);

    const rect = span.getBoundingClientRect();
    const top = rect.top + window.scrollY - textArea.scrollTop;
    const left = rect.left + window.scrollX - textArea.scrollLeft;
    document.body.removeChild(span);

    return { top, left };
  };

  return (
    <div className="main-container">
      <div className="user-list-container">
        <h3>Active Users</h3>
        <ul>
          {activeUsers.map((user, index) => (
            <li key={index}>{user}</li>
          ))}
        </ul>
        {messages.length > 0 && (
          <div className="user-messages">
            {messages.map((message, index) => (
              <div key={index} className="message">
                {message}
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="editor-container">
        {!isUsernameSet ? (
          <div className="username-input">
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={handleUsernameChange}
              onKeyDown={handleUsernameSubmit}
            />
          </div>
        ) : (
          <>
            <input
              type="file"
              accept=".txt"
              onChange={handleFileUpload}
              className="file-upload"
            />
            <textarea
              className="editor"
              ref={textAreaRef}
              value={content}
              onChange={handleChange}
              onKeyUp={handleCursorPosition}
              placeholder="Start typing..."
            ></textarea>

            {typingUser && typingUser !== username && (
              <div className="typing-indicator">
                {typingUser} is editing...
              </div>
            )}

            {Object.entries(cursorPositions).map(([user, position]) => {
              if (user === username) return null;
              const { top, left } = getCursorPosition(position);
              return (
                <div
                  key={user}
                  className="cursor-marker"
                  style={{ top, left }}
                >
                  <div className="cursor-label">{user}</div>
                </div>
              );
            })}
          </>
        )}
      </div>
    </div>
  );
}

export default DocumentEditor;
