import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

function DocumentEditor() {
  const [content, setContent] = useState("");
  const [users, setUsers] = useState([]); // Track connected users
  const [userName, setUserName] = useState(""); // Optional: Name for the user

  useEffect(() => {
    setUserName("User" + Math.floor(Math.random() * 1000)); // Random username
  }, []);

  useEffect(() => {
    socket.on("document", (newContent) => {
      setContent(newContent); // Update the document content in real-time
    });

    socket.on("users", (userList) => {
      setUsers(userList); // Update user list
    });

    // Emit user presence on connect
    socket.emit("userJoin", userName);

    return () => {
      socket.disconnect(); // Clean up socket connection on unmount
    };
  }, [userName]);

  const handleChange = (event) => {
    setContent(event.target.value);
    socket.emit("document", event.target.value); // Send updated content to the server
  };

  return (
    <div className="editor-container">
      <textarea
        className="editor"
        value={content}
        onChange={handleChange}
        placeholder="Start typing..."
      ></textarea>
      <div className="user-list">
        <h3>Users Online:</h3>
        <ul>
          {users.map((user, index) => (
            <li key={index}>{user.name || `User ${user.id.slice(0, 5)}`}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default DocumentEditor;
